tempsDate<-format(as.Date(as.vector(as.matrix(DATE[1,1:ncol(DATE)])), format = "%m/%d/%Y"),format="%d-%b-%Y")
J<-as.numeric(format(as.Date(as.vector(as.matrix(DATE[1,1:ncol(DATE)])), format = "%m/%d/%Y"),format="%j"))

