#####

#####Salmon distributions#####
#Here, we are applying Clear Creek spring-run Chinook
#see Table S3.1 and S3.2 in supplementary materials from this article
#phenology, based on Julian day
arrival  <- rnorm(1000, mean = 164, sd = 26.4) #normal
Spawning <- rnorm(1000, mean = 276, sd = 10.9) #normal
#spatial, based on RKM
reardistrib         <- runif(1000, min=0,    max=29.5) #uniform
spatialreddsdistrib <- runif(1000, min=12.5, max=29.5) #uniform
#####

#####ADULT HOLDING ENERGY EXPENDITURE#####
adholdreps <- replicate(1000, {
#parameters
j = 1.358442*(10^-5) #[MJ/mgO2] to convert from mgO2 kg-1 to MJ kg-1: 1 mgO2 = 1.358442*10-5 MJ
c = 65.21*24 #mgO2/d/kg #Intercept for mass-specific maintenance rate; Martin et al. 2015
b = -0.217 #Mass exponent for mass-specific maintenance rate #unitless #Martin et al. 2015
d = 0.068 #C^-1 #Temperature exponent for maintenance rate; Martin et al. 2015
M = 7.37 #kg #avg female mass from Bowerman et al. 2017

#length of holding period, the recursive index
#replaces days prior to 30d before arrival and 30d after last spawning day as NA
#in other words, assumes that salmon do not arrive much earlier than data shows, or after last spawning date
holdper <- J
for (i in 1:length(holdper)){
  if (holdper[i] < (min(arrival)-30)){
    holdper[i] <- NA
  }else if(holdper[i] > (max(Spawning)+30)){
    holdper[i] <- NA
  }else{
    holdper[i] <- holdper[i]
  }
}

#assumes that arrivals from day 1 are holding until randomly picked spawning date, and arrivals after pk spawning do not hold through day 366
#Spawning is spawning phenology distribution
randomSpawningDate <- sample(Spawning,1,replace=FALSE)
print(randomSpawningDate)
holdper = randomSpawningDate - holdper
for (i in 1:length(holdper)){
  if(is.na(holdper[i]) == TRUE){
    holdper[i] <- NA
  }else if(holdper[i] < 0){
    holdper[i] <- 0
  }else{
    holdper[i] <- holdper[i]
  }
}
holdper.func <- function(coln) {
  holdper[coln]
}

#function and recursion
P<- function(temp){return (j*c*(M^b)*exp(d*temp))} #temp is value from the matrix

recurRow <- function(rown, coln, netSum, RIn){
  if(RIn==0){
    return (netSum)
  }else if(coln==length(holdper)){
    return(netSum)
  }else{
    recurRow(rown, coln+1, P(temp = data[rown,coln])+netSum, RIn-1)
  }
}
#rown is the row number we are adding first, 
  #coln is the col number, 
  #netSum is the added value across rows, or net sum for number of reps so far, 
  #and RIn is how many times we've gone through so far (start with RIn=recurIndex, subtract 1 each time, end with 0)
#may need to add a wrap-around part depending on your needs

recurWholeRowHelper <- function(rown, coln, vect){
  if(coln > ncol(data)){
    return (vect)
  }else{
    Holdingper<-as.integer(holdper.func(coln))
    if(is.na(Holdingper) == TRUE){
      val <- NA
    }else{
      val <- recurRow(rown,coln, 0, Holdingper)
    }
    vect[coln] <- val
    recurWholeRowHelper(rown, coln+1,vect)
    
  }
}

recurWholeRow <- function(rown){ recurWholeRowHel']

